import React from 'react';
import '../styles/Program.css';  // Pastikan path ke file CSS benar

const Program = () => {
  const programs = [
    {
      id: 1,
      title: "Program 1",
      description: "Deskripsi program pertama yang menawarkan layanan X.",
      image: "/images/program1.jpg", // Ganti dengan path gambar yang sesuai
    },
    {
      id: 2,
      title: "Program 2",
      description: "Deskripsi program kedua yang menawarkan layanan Y.",
      image: "/images/program2.jpg", // Ganti dengan path gambar yang sesuai
    },
    {
      id: 3,
      title: "Program 3",
      description: "Deskripsi program ketiga yang menawarkan layanan Z.",
      image: "/images/program3.jpg", // Ganti dengan path gambar yang sesuai
    },
  ];

  return (
    <div className="program-container">
      <h2 className="page-title">Program Kami</h2>
      <div className="programs-list">
        {programs.map((program) => (
          <div key={program.id} className="program-card">
            <img
              src={program.image}
              alt={program.title}
              className="program-image"
            />
            <div className="program-details">
              <h3>{program.title}</h3>
              <p>{program.description}</p>
              <button className="program-button">Lihat Detail</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Program;
